mapper.select:
select p.ptitle, d.orderid, d.ea, d.saleprice
from tbl_orderdetail d join tbl_product p on d.productid = p.productid
where d.orderid = #{orderid}

mapper.insert:
insert into tbl_orderdetail(orderid, productid, ea, saleprice)
	select #{orderid}, #{productid}, #{productstock}, #{price}
		from tbl_cart
